CREATE TABLE `book` (
                        `ID` bigint unsigned NOT NULL AUTO_INCREMENT COMMENT '书籍ID',
                        `TITLE` varchar(255) NOT NULL COMMENT '书名',
                        `AUTHOR` varchar(100) NOT NULL COMMENT '作者',
                        `CATEGORY` varchar(50) DEFAULT NULL COMMENT '分类',
                        `PRICE` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '价格',
                        `PUBLISH_DATE` date DEFAULT NULL COMMENT '出版日期',
                        `STATUS` tinyint NOT NULL DEFAULT '1' COMMENT '状态：1-在售，2-下架，3-缺货',
                        `CREATE_TIME` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
                        `UPDATE_TIME` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
                        `BOOK_COUNT` int NOT NULL DEFAULT '0',
                        `AGE_CATEGORY` varchar(50) DEFAULT NULL,
                        `ACTIVE` tinyint DEFAULT NULL,
                        `BOOK_LANGUAGE` varchar(50) DEFAULT NULL,
                        PRIMARY KEY (`ID`),
                        KEY `idx_author` (`AUTHOR`),
                        KEY `idx_publish_date` (`PUBLISH_DATE`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='书籍信息表'

